import messagingSvc from "../../../common/services/messaging.svc";
import connectors from "../../../extension/background-script/connectors";

function handleTemp() {
  messagingSvc.onMessage("temp", async (msg) => {
    console.log("############# temp", msg);
    try {
      const lndConnector = new connectors.lnd({
        macaroon: "",
        url: "https://localhost:10080",
      });
      const info = await lndConnector.getInfo();
      console.log("##### info", info);
    } catch (err) {
      console.log("##### err", err);
    }
  });
}

export default handleTemp;
